package knightingale;

public class Vowel {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='b';
		if(ch=='a' || ch=='e' || ch=='i' || ch=='o' || ch=='u')
			System.out.println(ch+ " is a vowel");
		else
			System.out.println(ch+ " is not a vowel");

	}

}
